import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';  // For graphing the ECG data
import Chart from 'chart.js/auto';

const RealTimeECG = () => {
  const [ecgData, setEcgData] = useState([]);  // Raw ECG data
  const [filteredData, setFilteredData] = useState([]);  // Smoothed ECG data
  const [peaks, setPeaks] = useState([]);  // R-wave peaks
  const [ws, setWs] = useState(null);  // WebSocket connection

  useEffect(() => {
    // WebSocket connection setup
    let websocket = new WebSocket('ws://127.0.0.1:8080');

    websocket.onopen = () => {
      console.log('WebSocket connected!');
    };

    websocket.onmessage = (event) => {
      const rawData = parseInt(event.data, 10);  // Parse ECG value

      // Check that the data is not zero or an unexpected value
      if (rawData !== 0) {
        setEcgData((prevData) => [...prevData, rawData]);  // Store raw data
      }

      // Apply smoothing to the ECG data
      const smoothedData = smoothSignal(rawData);
      setFilteredData((prevData) => [...prevData, smoothedData]);

      // Detect peaks in the smoothed signal
      detectPeaks(smoothedData);
    };

    websocket.onclose = () => {
      console.log('WebSocket closed');
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, []);

  // Moving average filter to smooth the signal
  const smoothSignal = (newValue) => {
    const windowSize = 10;  // Moving average window size
    let smoothedData = [...filteredData, newValue].slice(-windowSize);  // Keep last 'windowSize' number of data points
    let average = smoothedData.reduce((acc, val) => acc + val, 0) / smoothedData.length;
    return average;
  };

  // Basic thresholding method to detect R-wave peaks
  const detectPeaks = (smoothedValue) => {
    const threshold = 3000;  // Set a threshold to identify peaks (adjust this)
    if (smoothedValue > threshold) {
      setPeaks((prevPeaks) => [...prevPeaks, smoothedValue]);
    }
  };

  // Prepare chart data
  const chartData = {
    labels: Array(ecgData.length).fill(''),
    datasets: [
      {
        label: 'Raw ECG Signal',
        data: ecgData,
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
        fill: false,
      },
      {
        label: 'Filtered ECG Signal',
        data: filteredData,
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
        fill: false,
      },
      {
        label: 'Detected R-Waves',
        data: peaks,
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
        borderWidth: 1,
        pointRadius: 5,
        pointHoverRadius: 7,
        fill: false,
      },
    ],
  };

  return (
    <div className="App">
      <h1>Real-Time ECG Monitor</h1>
      <Line data={chartData} />
      <p>Raw ECG Data Count: {ecgData.length}</p>
      <p>Filtered ECG Data Count: {filteredData.length}</p>
      <p>Detected Peaks: {peaks.length}</p>
    </div>
  );
};

export default RealTimeECG;
