import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8080');  // Connect to WebSocket server

    // When the WebSocket opens
    ws.onopen = () => {
      console.log("WebSocket connected!");
    };

    // When data is received from WebSocket
    ws.onmessage = (event) => {
      const value = parseInt(event.data, 10);  // Convert the received data to integer
      setData(prevData => {
        const updatedData = [...prevData, value];
        if (updatedData.length > 100) {  // Limit data to 100 points on the graph
          updatedData.shift();  // Remove the first value if there are more than 100 points
        }
        return updatedData;
      });
    };

    // When the WebSocket closes
    ws.onclose = () => {
      console.log("WebSocket disconnected! Attempting to reconnect...");
      setTimeout(() => {
        ws = new WebSocket('ws://localhost:8080');  // Reconnect on close
      }, 1000);  // Reconnect after 1 second
    };

    // On WebSocket error
    ws.onerror = (error) => {
      console.log("WebSocket error:", error);
    };

    // Clean up the WebSocket when the component is unmounted
    return () => {
      ws.close();
    };
  }, []);

  // Chart.js data
  const chartData = {
    labels: Array(data.length).fill(''),  // Just create empty labels for the X-axis
    datasets: [
      {
        label: 'ECG Data',
        data: data,
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
        fill: false,
      },
    ],
  };

  return (
    <div className="App">
      <h1>Real-Time ECG Monitor</h1>
      <Line data={chartData} />
    </div>
  );
}

export default App;
