import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function App() {
  const [ecgData, setEcgData] = useState(null);

  useEffect(() => {
    const socket = io('http://localhost:8080');  // Connect to the server

    // Log connection and disconnection
    socket.on('connect', () => {
      console.log('Socket connected!');
      socket.send('Hello from client!');  // Send a test message
    });

    socket.on('message', (data) => {
      console.log('Received from server:', data);  // Log server response
      setEcgData(data);  // Set data for display
    });

    socket.on('disconnect', () => {
      console.log('Socket disconnected');
    });

    return () => {
      socket.disconnect();  // Clean up the connection on component unmount
    };
  }, []);

  return (
    <div className="App">
      <h1>Real-Time ECG Monitor</h1>
      {ecgData ? (
        <div>
          <h2>{ecgData}</h2>
        </div>
      ) : (
        <p>Waiting for ECG data...</p>
      )}
    </div>
  );
}

export default App;
