import React, { useState, useEffect } from 'react';

function App() {
  const [ecgData, setEcgData] = useState(null);
  const [ws, setWs] = useState(null);  // Save the WebSocket instance

  useEffect(() => {
    const socket = new WebSocket('ws://localhost:8080');  // Establish WebSocket connection

    socket.onopen = () => {
      console.log("WebSocket connected!");
    };

    socket.onmessage = (event) => {
      console.log("Received data from WebSocket:", event.data);  // Log received data
      setEcgData(event.data);  // Update state with the received data
    };

    socket.onclose = () => {
      console.log("WebSocket disconnected! Attempting to reconnect...");
      setTimeout(() => {
        setWs(new WebSocket('ws://localhost:8080'));  // Reconnect after 1 second
      }, 1000);
    };

    socket.onerror = (error) => {
      console.log("WebSocket error:", error);
    };

    // Save the WebSocket instance in the state
    setWs(socket);

    // Clean up the WebSocket connection when the component is unmounted
    return () => {
      socket.close();
    };
  }, []);

  return (
    <div className="App">
      <h1>Real-Time ECG Monitor</h1>
      {ecgData ? (
        <div>
          <h2>ECG Value: {ecgData}</h2>
        </div>
      ) : (
        <p>Waiting for ECG data...</p>
      )}
    </div>
  );
}

export default App;
