import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8080');  // Connect to WebSocket server

    ws.onopen = () => {
      console.log("WebSocket connected!");
    };

    ws.onmessage = (event) => {
      console.log("Received data from WebSocket:", event.data);  // Log received data

      // Ensure data is numeric and valid
      const value = parseInt(event.data, 10);
      if (!isNaN(value)) {  // If the value is numeric, add it to the data state
        setData(prevData => {
          const updatedData = [...prevData, value];
          if (updatedData.length > 100) {  // Limit to 100 points on the graph
            updatedData.shift();  // Remove the first value if more than 100 points
          }
          return updatedData;
        });
      } else {
        console.error("Non-numeric data received:", event.data);  // Log if data is not numeric
      }
    };

    ws.onclose = () => {
      console.log("WebSocket disconnected! Attempting to reconnect...");
      setTimeout(() => {
        ws = new WebSocket('ws://localhost:8080');  // Reconnect after 1 second
      }, 1000);  // Reconnect after 1 second
    };

    ws.onerror = (error) => {
      console.log("WebSocket error:", error);
    };

    // Clean up the WebSocket connection on component unmount
    return () => {
      ws.close();
    };
  }, []);  // Empty dependency array to run only once when the component mounts

  // Chart.js data
  const chartData = {
    labels: Array(data.length).fill(''),  // Just create empty labels for the X-axis
    datasets: [
      {
        label: 'ECG Data',
        data: data,
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
        fill: false,
      },
    ],
  };

  // Log chart data for debugging
  console.log("Chart data:", chartData);

  return (
    <div className="App">
      <h1>Real-Time ECG Monitor</h1>
      <Line data={chartData} />
    </div>
  );
}

export default App;
